import React, { Component } from 'react';
import { Row, Container, Col, Card } from 'react-bootstrap';
import Title from './Title';
import PlanetCard from './PlanetCard';
import Planets from '../data/planets';
import './Planet-cards.css';

class SolarSystem extends Component {
  render() {
    return (
      <div data-testid="solar-system">
        <Title headline="Planetas" />
        <Container fluid>
          <Row className="container justify-content-md-center">
            {
              Planets
                .map(
                  (
                    { name, image },
                  ) => (
                    <Col key={ name } className="justify-content-center">
                      <Card
                        className="bg-light text-dark mb-3"
                        style={ { width: '18rem' } }
                      >
                        <Card.Img variant="top" src={ image } />
                        <Card.Body>
                          <Card.Title>{ name }</Card.Title>
                        </Card.Body>
                      </Card>
                    </Col>
                    // <Col
                    //   className="mb-3 view overlay Planet-cards"
                    //   key={ name }
                    //   md="auto"
                    // >
                    //   <PlanetCard
                    //     className="Planet-cards"
                    //     key={ name }
                    //     planetImage={ image }
                    //     planetName={ name }
                    //   />
                    // </Col>
                  ),
                )
            }
          </Row>
        </Container>
      </div>
    );
  }
}

export default SolarSystem;
