import React from 'react';

const Select = ({value, name, handleChange, optionValue, optionName=''}) => {
  return (
    <select
        value={ value }
        name={ name }
        onChange={ handleChange }
      >
        <option value="">{optionName}</option>
        { optionValue.lenght > optionValue.map((option) => (
          <option
            key={ option }
            value={ option }
          >
            { option }
          </option>
        )) }
      </select>
  );
}

export default Select;
