import PropTypes from 'prop-types';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getCurrency, SetExpenses } from '../actions/index';

class Form extends Component {
  constructor() {
    super();
    this.state = {
      id: 0,
      value: '',
      description: '',
      currency: 'USD',
      method: '',
      tag: '',
      exchangeRates: {},
    };
  }

  componentDidMount() {
    const { getMapCurrency } = this.props;
    getMapCurrency();
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  clearInputs = () => {
    this.setState({
      value: '',
      description: '',
      currency: 'USD',
      method: '',
      tag: '',
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { id } = this.state;
    const { addExpense, currencyFromGlobalStore, getMapCurrency } = this.props;
    this.setState({ id: id + 1 });
    addExpense({
      ...this.state,
      exchangeRates: currencyFromGlobalStore,
    });
    getMapCurrency();
    this.clearInputs();
  };

  setExchangeRates = () => {
    const { currencyFromGlobalStore } = this.props;
    this.setState({ exchangeRates: currencyFromGlobalStore });
  }

  render() {
    const { value, description, currency, method, tag } = this.state;
    const { currencyFromGlobalStore } = this.props;
    const { handleChange, handleSubmit } = this;
    return (
      <div>
        <input
          className="px-2 placeholder:italic w-14 border-b-2 border-gray-600 mr-1 rounded-md focus:ring-1 focus:outline-none focus:ring-gray-500 shadow-sm"
          placeholder="Valor"
          name="value"
          value={ value }
          type="text"
          data-testid="value-input"
          onChange={ (e) => handleChange(e) }
        />
        <input
          className="px-2 placeholder:italic w-fit border-b-2 border-gray-600 mr-1 rounded-md focus:ring-1 focus:outline-none focus:ring-gray-500 shadow-sm"
          place
          name="description"
          value={ description }
          data-testid="description-input"
          onChange={ (e) => handleChange(e) }
        />
        <label htmlFor="currency" aria-label="Moeda">
          <select
            id="currency"
            value={ currency }
            name="currency"
            data-testid="currency-input"
            onChange={ (e) => handleChange(e) }
          >
            { Object.keys(currencyFromGlobalStore)
              .filter((actualCurrency) => actualCurrency !== 'USDT')
              .map((actualCurrency) => (
                <option
                  data-testid={ actualCurrency }
                  key={ actualCurrency }
                  value={ actualCurrency }
                >
                  { actualCurrency }
                </option>
              )) }
          </select>
        </label>
        <select
          id="method"
          value={ method }
          name="method"
          data-testid="method-input"
          onChange={ handleChange }
        >
          <option value="">Choose a payment method</option>
          <option value="Cartão de crédito">Cartão de crédito</option>
          <option value="Cartão de débito">Cartão de débito</option>
          <option value="Dinheiro">Dinheiro</option>
        </select>
        <label htmlFor="tag">
          <select
            value={ tag }
            name="tag"
            id="tag"
            data-testid="tag-input"
            onChange={ handleChange }
          >
            <option value="">Choose a tag</option>
            <option value="Alimentação">Alimentação</option>
            <option value="Trabalho">Trabalho</option>
            <option value="Lazer">Lazer</option>
            <option value="Saúde">Saúde</option>
            <option value="Transporte"> Transporte</option>
          </select>
        </label>
        <button
          type="submit"
          onClick={ (e) => {
            handleSubmit(e);
          } }
        >
          Adicionar despesa
        </button>
      </div>
    );
  }
}

Form.propTypes = {
  addExpense: PropTypes.func.isRequired,
  getMapCurrency: PropTypes.func.isRequired,
  currencyFromGlobalStore: PropTypes.shape({
    USD: PropTypes.string.isRequired,
  }).isRequired,
};

function mapStateToProps(state) {
  return {
    currencyFromGlobalStore: state.wallet.currencies,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    addExpense: (expense) => dispatch(SetExpenses(expense)),
    getMapCurrency: () => dispatch(getCurrency()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Form);
